mapper.select:
SELECT
		GUID	as "guid",  
		NAME	as "name",  
		COLUMNS	as "columns",  
		ALGORITHM	as "algorithm",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser" 
FROM T_OC_TABLERULE

	
		and GUID = #{guid}
	
	
		and NAME = #{name}
	
	
		and COLUMNS = #{columns}
	
	
		and ALGORITHM = #{algorithm}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
	
	
		and ${whereex}

SELECT COUNT(*) FROM T_OC_TABLERULE 

	
		and GUID = #{guid}
	
	
		and NAME = #{name}
	
	
		and COLUMNS = #{columns}
	
	
		and ALGORITHM = #{algorithm}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
