mapper.select:
SELECT
		guid	as "guid",  
		product_code	as "productCode",  
		product_name	as "productName",  
		company	as "company",  
		product_digest	as "productDigest",  
		product_type	as "productType",  
		biz_field	as "bizField",  
		product_manager	as "productManager",  
		manager_phone	as "managerPhone",  
		address	as "address",  
		customer_service	as "customerService",  
		operation_phone	as "operationPhone",  
		mobile_phone	as "mobilePhone",  
		create_time	as "createTime",  
		create_user	as "createUser" 
FROM t_sa_product

	
		and guid = #{guid}
	
	
		and product_code = #{productCode}
	
	
		and product_name like '%${productName}%'
	
	
		and company like '%${company}%'
	
	
		and product_digest = #{productDigest}
	
	
		and product_type = #{productType}
	
	
		and biz_field = #{bizField}
	
	
		and product_manager = #{productManager}
	
	
		and manager_phone = #{managerPhone}
	
	
		and address = #{address}
	
	
		and customer_service = #{customerService}
	
	
		and operation_phone = #{operationPhone}
	
	
		and mobile_phone = #{mobilePhone}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}

SELECT COUNT(*) FROM t_sa_product 

	
		and guid = #{guid}
	
	
		and product_code = #{productCode}
	
	
		and product_name like '%${productName}%'
	
	
		and company like '%${company}%'
	
	
		and product_digest = #{productDigest}
	
	
		and product_type = #{productType}
	
	
		and biz_field = #{bizField}
	
	
		and product_manager = #{productManager}
	
	
		and manager_phone = #{managerPhone}
	
	
		and address = #{address}
	
	
		and customer_service = #{customerService}
	
	
		and operation_phone = #{operationPhone}
	
	
		and mobile_phone = #{mobilePhone}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}

SELECT product_code as "code", product_name as "name" FROM t_sa_product
and product_code = #{code}
and product_name like '%${name}%'
