mapper.select:
SELECT
		t.GUID	as "guid",  
		t.USER_GUID	as "userGuid",  
		t.USER_CODE	as "userCode",  
		t.ROLE_GUID	as "roleGuid",  
		t.ROLE_CODE	as "roleCode",  
		(select r.ROLE_NAME from T_SYS_ROLE r where t.ROLE_GUID = r.guid) as "roleName",  
		t.CREATE_TIME	as "createTime",  
		t.CREATE_USER	as "createUser",  
		t.MODIFY_TIME	as "modifyTime",  
		t.MODIFY_USER	as "modifyUser" 
FROM T_SYS_AUTHUSER t

	
		and t.GUID = #{guid}
	
	
		and t.USER_GUID = #{userGuid}
	
	
		and t.ROLE_GUID = #{roleGuid}
	
	
		and t.USER_CODE = #{userCode}
	
	
		and t.CREATE_TIME = #{createTime}
	
	
		and t.ROLE_CODE = #{roleCode}
	
	
		and t.CREATE_USER = #{createUser}
	
	
		and t.MODIFY_TIME = #{modifyTime}
	
	
		and t.MODIFY_USER = #{modifyUser}

SELECT COUNT(*) FROM T_SYS_AUTHUSER 

	
		and GUID = #{guid}
	
	
		and USER_GUID = #{userGuid}
	
	
		and ROLE_GUID = #{roleGuid}
	
	
		and USER_CODE = #{userCode}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and ROLE_CODE = #{roleCode}
	
	
		and CREATE_USER = #{createUser}
	
	
		and MODIFY_TIME = #{modifyTime}
	
	
		and MODIFY_USER = #{modifyUser}
