mapper.select:
SELECT
		guid	as "guid",  
		code	as "code",  
		parent_code	as "parentCode",  
		page_code	as "pageCode",  
		remark	as "remark",  
		create_time	as "createTime",  
		create_user	as "createUser",  
		status	as "status",  
		sort_index	as "sortIndex",  
		name	as "name", 
		state as "state" 
FROM t_sys_menu

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and page_code = #{pageCode}
	
	
		and remark = #{remark}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and status = #{status}
	
	
		and sort_index = #{sortIndex}
	
	
		and name like '%${name}%'
	

order by sort_index

SELECT b.is_cache as "isCache", a.code as "id", a.parent_code as "parentCode", a.name as "text", a.state as "state", b.url as "url", b.name as "pageName" FROM t_sys_menu a LEFT JOIN t_sys_page b ON a.page_code = b.code
a.code in (select s.resource_code from t_sys_authresouce s, t_sys_authuser u where s.role_guid = u.role_guid and u.user_code = #{loginId})
and (a.parent_code = '' OR a.parent_code IS null)
and a.parent_code = #{id}
and a.code = #{code}
order by sort_index

SELECT COUNT(*) FROM t_sys_menu 

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and parent_code = #{parentCode}
	
	
		and page_code = #{pageCode}
	
	
		and remark = #{remark}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and status = #{status}
	
	
		and sort_index = #{sortIndex}
	
	
		and name like '%${name}%'
