mapper.select:
SELECT
		GUID	as "guid",  
		NS_CODE	as "nsCode",  
		NS_NAME	as "nsName",  
		ST_CODE	as "stCode",  
		ST_NAME	as "stName",  
		REMARK	as "remark",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser" 
FROM T_SYS_MYBATIS

	
		and GUID = #{guid}
	
	
		and NS_CODE = #{nsCode}
	
	
		and NS_NAME LIKE '%${nsName}%'
	
	
		and ST_CODE = #{stCode}
	
	
		and ST_NAME LIKE '%${stName}%'
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}

SELECT 
		DISTINCT NS_CODE	as "nsCode",
		NS_NAME	as "nsName"
FROM T_SYS_MYBATIS

	
		and GUID = #{guid}
	
	
		and NS_CODE = #{nsCode}
	
	
		and NS_NAME LIKE '%${nsName}%'
	
	
		and ST_CODE = #{stCode}
	
	
		and ST_NAME LIKE '%${stName}%'
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}

SELECT  COUNT(DISTINCT NS_CODE) FROM T_SYS_MYBATIS

	
		and GUID = #{guid}
	
	
		and NS_CODE = #{nsCode}
	
	
		and NS_NAME LIKE '%${nsName}%'
	
	
		and ST_CODE = #{stCode}
	
	
		and ST_NAME LIKE '%${stName}%'
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}

SELECT COUNT(*) FROM T_SYS_MYBATIS 

	
		and GUID = #{guid}
	
	
		and NS_CODE = #{nsCode}
	
	
		and NS_NAME LIKE '%${nsName}%'
	
	
		and ST_CODE = #{stCode}
	
	
		and ST_NAME LIKE '%${stName}%'
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
