mapper.select:
SELECT
		guid	as "guid",  
		code	as "code",  
		url	as "url",  
		name	as "name",  
		is_cache	as "isCache",  
		create_time	as "createTime",  
		create_user	as "createUser" 
FROM t_sys_page

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and url = #{url}
	
	
		and name like '%${name}%'
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and is_cache = #{isCache}

SELECT COUNT(*) FROM t_sys_page 

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and url = #{url}
	
	
		and name like '%${name}%'
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and is_cache = #{isCache}

mapper.update:
UPDATE t_sys_page  

	
		guid = #{guid},
	
	
		code = #{code},
	
	
		url = #{url},
	
	
		name = #{name},
	
	
		is_cache = #{isCache}
	
	
		create_time = #{createTime},
	
	
		create_user = #{createUser}
	


	guid = #{guid}
