mapper.select:
SELECT b.GUID as "guid", a.DATAHOST as "datahost", a.HOST as "host", b.URL as "url", b.D_USER "user", b.PASSWORD "password", c.DBTYPE as "dbType", c.DBDRIVER as "dbDriver" FROM T_OC_DATAHOSTMAP a,T_OC_HOST b,t_oc_datahost c where a.HOST = b.HOST and c.NAME = a.DATAHOST
and a.DATAHOST = #{dataHost}
and a.HOST = #{host}

		SELECT count(*)
		FROM T_OC_DATAHOSTMAP a,T_OC_HOST b,t_oc_datahost c
                where a.HOST = b.HOST and c.NAME = a.DATAHOST
		
			
				and a.DATAHOST = #{dataHost}
			
			
				and a.HOST = #{host}

