mapper.select:
SELECT
		guid	as "guid",  
		code	as "code",  
		name	as "name",  
		linkman	as "linkman",  
		telephony	as "telephony",  
		e_mail	as "eMail",  
		create_time	as "createTime",  
		create_user	as "createUser",  
		remark	as "remark" 
FROM t_sa_customer

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and name like '%${name}%'
	
	
		and linkman = #{linkman}
	
	
		and telephony = #{telephony}
	
	
		and e_mail = #{eMail}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and remark = #{remark}

SELECT COUNT(*) FROM t_sa_customer 

	
		and guid = #{guid}
	
	
		and code = #{code}
	
	
		and name like '%${name}%'
	
	
		and linkman = #{linkman}
	
	
		and telephony = #{telephony}
	
	
		and e_mail = #{eMail}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and remark = #{remark}

SELECT code as "code", name as "name" FROM t_sa_customer
and code = #{code}
and name like '%${name}%'
