mapper.select:
SELECT
		guid	as "guid",  
		org_code	as "orgCode",  
		org_name	as "orgName",  
		parent_code	as "parentCode",  
		levels	as "level",  
		alive_flag	as "aliveFlag",  
		create_time	as "createTime",  
		create_user	as "createUser",  
		state	as "state" 
FROM t_sys_org

	
		and guid = #{guid}
	
	
		and org_code = #{orgCode}
	
	
		and org_name like '%${orgName}%'
	
	
		and parent_code = #{parentCode}
	
	
		and levels = #{level}
	
	
		and alive_flag = #{aliveFlag}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
	
	
		and state = #{state}

SELECT guid as "guid", org_code as "orgCode", org_name as "orgName", parent_code as "parentCode", levels as "level", alive_flag as "aliveFlag", create_time as "createTime", create_user as "createUser", state as "state" FROM t_sys_org
and parent_code = '' OR parent_code IS null
and parent_code = #{id}

SELECT org_code as "id", org_name as "text", state as "state" FROM t_sys_org
and (parent_code = '' OR parent_code IS null)
and parent_code = #{id}
and org_code = #{orgCode}
and org_name like '%${orgName}%'

SELECT org_code as "id", org_name as "text", state as "state" FROM t_sys_org
and org_code = #{orgCode}
and org_name like '%${orgName}%'

		SELECT COUNT(*) FROM t_sys_org 
		
			
				and guid = #{guid}
			
			
				and org_code = #{orgCode}
			
			
				and org_name like '%${orgName}%'
			
			
				and parent_code = #{parentCode}
			
			
				and levels = #{level}
			
			
				and alive_flag = #{aliveFlag}
			
			
				and create_time = #{createTime}
			
			
				and create_user = #{createUser}
