mapper.select:
SELECT
		t.guid	as "guid",  
		t.role_code	as "roleCode",  
		t.role_name	as "roleName",  
		t.role_type_code	as "roleTypeCode",  
		(select r.role_type_name from t_sys_roletype r where t.role_type_code = r.role_type_code) as "roleTypeName", 
		t.org_code	as "orgCode",  
		(select o.org_name from t_sys_org o where o.org_code = t.org_code) as "orgName", 
		t.create_time	as "createTime",  
		t.create_user	as "createUser" 
FROM t_sys_role t

	
		and t.guid = #{guid}
	
	
		and t.role_code = #{roleCode}
	
	
		and t.role_name like '%${roleName}%'
	
	
		and t.role_type_code = #{roleTypeCode}
	
	
		and t.org_code = #{orgCode}
	
	
		and t.create_time = #{createTime}
	
	
		and t.create_user = #{createUser}

SELECT COUNT(*) FROM t_sys_role 

	
		and guid = #{guid}
	
	
		and role_code = #{roleCode}
	
	
		and role_name like '%${roleName}%'
	
	
		and role_type_code = #{roleTypeCode}
	
	
		and org_code = #{orgCode}
	
	
		and create_time = #{createTime}
	
	
		and create_user = #{createUser}
