mapper.select:
SELECT
		GUID	as "guid",  
		TITLE	as "title",  
		TYPE	as "type",  
		FUN_MODULES	as "funModules",  
		SUGGESTION	as "suggestion",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser",  
		REPLY	as "reply",  
		SUG_LEVEL	as "sugLevel",  
		SUG_STATUS	as "sugStatus" 
FROM T_SYS_SUGGESTION

	
		and GUID = #{guid}
	
	
		and TITLE like '%${title}%'
	
	
		and TYPE = #{type}
	
	
		and FUN_MODULES = #{funModules}
	
	
		and SUGGESTION = #{suggestion}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
	
	
		and REPLY = #{reply}
	
	
		and SUG_LEVEL = #{sugLevel}
	
	
		and SUG_STATUS = #{sugStatus}
	
	
		and ${whereex}

SELECT COUNT(*) FROM T_SYS_SUGGESTION 

	
		and GUID = #{guid}
	
	
		and TITLE like '%${title}%'
	
	
		and TYPE = #{type}
	
	
		and FUN_MODULES = #{funModules}
	
	
		and SUGGESTION = #{suggestion}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
	
	
		and REPLY = #{reply}
	
	
		and SUG_LEVEL = #{sugLevel}
	
	
		and SUG_STATUS = #{sugStatus}
	
	
		and ${whereex}
