mapper.select:
SELECT
		SORT_INDEX	as "sortIndex",  
		WHERE_CODE	as "whereCode",  
		GUID	as "guid",  
		WHERE_NAME	as "whereName",  
		CODE	as "code",  
		DESCRIBE	as "describe",  
		REMARK	as "remark",  
		CREATE_TIME	as "createTime",  
		CREATE_USER	as "createUser",  
		VALUE_TYPE	as "valueType",  
		VALUE_CODE	as "valueCode",  
		VALUE_SERVICE	as "valueService",  
		VALUE_METHOD	as "valueMethod",  
		BIZ_COMBOX_ID	as "bizComboxId",  
		BIZ_COMBOX_TEXT	as "bizComboxText",  
		EXTEND3	as "extend3",  
		EXTEND4	as "extend4",  
		EXTEND5	as "extend5",  
		BIZ_COLUMNS	as "bizColumns" 
FROM T_SYS_WHEREEX

	
		and SORT_INDEX = #{sortIndex}
	
	
		and WHERE_CODE = #{whereCode}
	
	
		and GUID = #{guid}
	
	
		and WHERE_NAME = #{whereName}
	
	
		and CODE = #{code}
	
	
		and DESCRIBE = #{describe}
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
	
	
		and VALUE_TYPE = #{valueType}
	
	
		and VALUE_CODE = #{valueCode}
	
	
		and VALUE_SERVICE = #{valueService}
	
	
		and VALUE_METHOD = #{valueMethod}
	
	
		and BIZ_COMBOX_ID = #{bizComboxId}
	
	
		and BIZ_COMBOX_TEXT = #{bizComboxText}
	
	
		and EXTEND3 = #{extend3}
	
	
		and EXTEND4 = #{extend4}
	
	
		and EXTEND5 = #{extend5}
	
	
		and BIZ_COLUMNS = #{bizColumns}
	
	
		and ${whereex}
	

order by WHERE_CODE,SORT_INDEX

SELECT COUNT(*) FROM T_SYS_WHEREEX 

	
		and SORT_INDEX = #{sortIndex}
	
	
		and WHERE_CODE = #{whereCode}
	
	
		and GUID = #{guid}
	
	
		and WHERE_NAME = #{whereName}
	
	
		and CODE = #{code}
	
	
		and DESCRIBE = #{describe}
	
	
		and REMARK = #{remark}
	
	
		and CREATE_TIME = #{createTime}
	
	
		and CREATE_USER = #{createUser}
	
	
		and VALUE_TYPE = #{valueType}
	
	
		and VALUE_CODE = #{valueCode}
	
	
		and VALUE_SERVICE = #{valueService}
	
	
		and VALUE_METHOD = #{valueMethod}
	
	
		and BIZ_COMBOX_ID = #{bizComboxId}
	
	
		and BIZ_COMBOX_TEXT = #{bizComboxText}
	
	
		and EXTEND3 = #{extend3}
	
	
		and EXTEND4 = #{extend4}
	
	
		and EXTEND5 = #{extend5}
	
	
		and BIZ_COLUMNS = #{bizColumns}
	
	
		and ${whereex}
